package com.melonlee.ewd.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;@Controller@RequestMapping("/admin")public class AdminController {	@RequestMapping(value = "login", method = RequestMethod.GET)	public String loginPage(ModelMap model) {		model.addAttribute("message", "Hello world!--melon");		return "log";	}			@RequestMapping(value = "login", method = RequestMethod.POST)	public String login(ModelMap model) {		model.addAttribute("message", "Hello world!--melon");		return "index";	}	@RequestMapping(value = "logout", method = RequestMethod.GET)	public String logout(ModelMap model) {		return "log";	}}